///$tab Calendar
SUB CalendarGen(pDateField, pCalendarName, pFiscalMonthsLeft)
    // ------------------------------------------
    // PARAMETER MAPPING
    // ------------------------------------------
    LET vCal_DateField     = '$(pDateField)';
    LET vCal_TableName     = if('$(pCalendarName)' = '', 'Calendar', '$(pCalendarName)');
    LET vCal_FiscalOffset  = $(pFiscalMonthsLeft);
    LET vCal_Today         = Num(Today());
    // ------------------------------------------
    // FIND MIN/MAX FROM THE PASSED DATE FIELD
    // ------------------------------------------
    Calendar_MinMax:
    LOAD
        Num(Min(_DateScan)) AS MinDate,
        Num(Max(_DateScan)) AS MaxDate
    ;
    LOAD 
        FieldValue('$(vCal_DateField)', IterNo()) AS _DateScan
    AUTOGENERATE 1
    WHILE NOT IsNull(FieldValue('$(vCal_DateField)', IterNo()));
    LET vCal_MinDate = Peek('MinDate', 0, 'Calendar_MinMax');
    LET vCal_MaxDate = Peek('MaxDate', 0, 'Calendar_MinMax');
    DROP TABLE Calendar_MinMax;
    IF Len('$(vCal_MinDate)') = 0 OR Len('$(vCal_MaxDate)') = 0 THEN
        TRACE 'CalendarGen: No valid min/max dates found. Is $(vCal_DateField) a proper date field?';
        EXIT SUB;
    ENDIF

    CalendargenQuarterMap:
    MAPPING LOAD * INLINE [
        Month, Quarter
        1, Q1
        2, Q1
        3, Q1
        4, Q2
        5, Q2
        6, Q2
        7, Q3
        8, Q3
        9, Q3
        10, Q4
        11, Q4
        12, Q4
    ];

    // ------------------------------------------
    // GENERATE BASE CALENDAR
    // ------------------------------------------
    [$(vCal_TableName)]:
    LOAD*,
        Date($(vCal_MinDate) + RecNo() - 1) AS Date,

        Week([$(vCal_DateField)]) AS [Week],
        Year([$(vCal_DateField)]) AS [Year],
        Month([$(vCal_DateField)]) AS [Month],

        Day([$(vCal_DateField)]) AS [Day],
        DayNumberOfYear([$(vCal_DateField)]) AS DayOfYear,
        WeekDay([$(vCal_DateField)]) AS [WeekDay],
        applyMap('CalendargenQuarterMap', num(month([$(vCal_DateField)])),null()) AS [Quarter],
        Date(monthStart([$(vCal_DateField)]), 'MM-YYYY') AS [MonthYear],
        dual(applyMap('CalendargenQuarterMap', num(month([$(vCal_DateField)])),null())
            & '-' & Year([$(vCal_DateField)]),QuarterStart([$(vCal_DateField)])) AS [QuarterYear],
        dual(Week([$(vCal_DateField)]) & '-' & WeekYear([$(vCal_DateField)]),WeekStart([$(vCal_DateField)])) AS [WeekYear],

        IF(Year2Date($(vCal_DateField), 0, 1, $(vCal_Today)), 1, 0) AS [YTD Flag],
        IF(Year2Date($(vCal_DateField), -1, 1, $(vCal_Today)), 1, 0) AS [PYTD Flag],
        IF(Month($(vCal_DateField)) = Month($(vCal_Today)), 1, 0) AS [CurrentMonth Flag],
        IF(Month(AddMonths($(vCal_DateField),1)) = Month($(vCal_Today)), 1, 0) AS [LastMonth Flag],

        // Relative time comparisons
        Year(Today()) - Year($(vCal_DateField)) AS YearsAgo,
        4 * Year(Today()) + Ceil(Month(Today()) / 3) - 4 * Year($(vCal_DateField)) - Ceil(Month($(vCal_DateField)) / 3) AS QuartersAgo,
        12 * Year(Today()) + Month(Today()) - 12 * Year($(vCal_DateField)) - Month($(vCal_DateField)) AS MonthsAgo,
        If(WeekDay($(vCal_DateField))<=WeekDay(Today()),1,0) AS [InWTD],
        (WeekStart(Today())-WeekStart($(vCal_DateField)))/7 AS [WeeksAgo] ;
    LOAD
       Date($(vCal_MinDate) + RecNo() - 1) AS [$(vCal_DateField)]
    AUTOGENERATE ($(vCal_MaxDate) - $(vCal_MinDate) + 1);
    // ------------------------------------------
    // OPTIONAL: FISCAL CALENDAR
    // ------------------------------------------
    IF '$(vCal_FiscalOffset)' <> '' THEN
        IF $(vCal_FiscalOffset) < 0 THEN
            LET vCal_FiscalStartMonth = 13 - ($(vCal_FiscalOffset) + 12);
        ELSE
            LET vCal_FiscalStartMonth = 13 - $(vCal_FiscalOffset);
        ENDIF
        LEFT JOIN ([$(vCal_TableName)])
        LOAD 
            Date,
            Year(AddMonths(Date, $(vCal_FiscalOffset))) AS [Fiscal Year],
            Dual(Month(Date), Num(Month(AddMonths(Date, $(vCal_FiscalOffset))))) AS [Fiscal Month],
            'Q' & Ceil(Month(AddMonths(Date, $(vCal_FiscalOffset))) / 3) AS [Fiscal Quarter],
            Date(MonthStart(AddMonths(Date, $(vCal_FiscalOffset))), 'MM-YYYY') AS [Fiscal MonthYear],
            Dual(
                'Q' & Ceil(Month(AddMonths(Date, $(vCal_FiscalOffset))) / 3)
                & '-' &
                Year(AddMonths(Date, $(vCal_FiscalOffset))),
                QuarterStart(AddMonths(Date, $(vCal_FiscalOffset)))
            ) AS [Fiscal QuarterYear],
            IF(YearToDate(Date, 0, $(vCal_FiscalStartMonth), $(vCal_Today)), 1, 0) AS [Fiscal YTD Flag],
            IF(YearToDate(Date, -1, $(vCal_FiscalStartMonth), $(vCal_Today)), 1, 0) AS [Fiscal PYTD Flag]
        RESIDENT [$(vCal_TableName)];
    ENDIF
    // ------------------------------------------
    // DERIVED CALENDAR FIELDS
    // ------------------------------------------
    CalendarDerived:
        DECLARE FIELD DEFINITION Tagged ('$date')
        FIELDS
            Dual(Year($1), YearStart($1)) AS Year Tagged ('$axis', '$year'),
            Dual('Q' & Ceil(Month($1)/3), Ceil(Month($1)/3)) AS Quarter Tagged ('$axis', '$quarter'),
            Dual(Year($1) & '-Q' & Ceil(Month($1)/3), QuarterStart($1)) AS YearQuarter Tagged ('$axis', '$yearquarter'),
            Dual(Year($1), WeekStart($1)) & '-' & Num(Week($1), 00) AS YearWeek Tagged ('$axis', '$yearweek'),
            Month($1) AS Month Tagged ('$axis', '$month'),
            Dual(Year($1) & '-' & Month($1), MonthStart($1)) AS YearMonth Tagged ('$axis', '$yearmonth'),
            Dual('W' & Num(Week($1),00), Num(Week($1),00)) AS Week Tagged ('$axis', '$weeknumber'),
            Day($1) AS Day Tagged ('$axis', '$day'),
            Weekday($1) AS Weekday Tagged ('$axis', '$weekday')
        ;
    Derive Fields from Fields Date Using CalendarDerived;
    // ------------------------------------------
    // CLEANUP
    // ------------------------------------------
    Autonumber '$(vCal_DateField)';
    DROP VARIABLES vCal_DateField,vCal_TableName,vCal_FiscalOffset,vCal_Today,vCal_MinDate,vCal_MaxDate,vCal_FiscalStartMonth;

END SUB
///$tab Data load
SUB LoadData
$(vGitHubTableName):
LOAD 
    SHA,
    TextBetween(QVD,'of','.qvf') AS QVD,
    Timestamp(Timestamp#(Date, 'YYYY-MM-DDTHH:mm:ssZ') + 1/24) as ModifiedDate //Orgn. UTC timezone
INLINE [
SHA,QVD,Date
{ $.vInlineTable }
];
Concatenate($(vGitHubTableName))
Unless $(vIncrementalQVDsAvailable) = 0 LOAD 
*
FROM '$(vOutputQVDName($(vGitHubTableName)))' (qvd)
WHERE NOT Exists(SHA);
STORE $(vGitHubTableName) INTO '$(vOutputQVDName($(vGitHubTableName)))' (qvd);
END Sub
